import React, { useEffect, useState } from 'react';
import GronGardPlatform from './grongardplatform.js';
import './AppWrapper.css'; // Make sure to create this CSS file

function AppWrapper() {
  const [userType, setUserType] = useState(null);

  useEffect(() => {
    // Retrieve the user type from localStorage
    const storedUserType = localStorage.getItem('userType');
    if (storedUserType) {
      setUserType(storedUserType);
      // Clear the userType from localStorage after retrieving it
      localStorage.removeItem('userType');
    }
  }, []);

  const getUserTypeDisplay = () => {
    if (userType === 'customer') {
      return 'Kund';
    } else if (userType === 'provider') {
      return 'Leverantör';
    }
    return '';
  };

  return (
    <div className="app-wrapper">
      <header>
        <h1><span className="green">Grön</span>Gård</h1>
        <span className={`user-type ${userType}`}>{getUserTypeDisplay()}</span>
      </header>
      <main>
        <GronGardPlatform userType={userType} />
      </main>
      <footer>
        <p>&copy; 2024 GrönGård AB. Alla rättigheter förbehållna.</p>
      </footer>
    </div>
  );
}

export default AppWrapper;
