import React, { useState, useEffect } from 'react';
import { AlertCircle, CheckCircle, Loader, CreditCard, Smartphone, Search, Clipboard, DollarSign } from 'lucide-react';
import { Alert, AlertDescription, AlertTitle } from '../components/ui/alert';

// Mock data
const mockUsers = [
  { id: 1, name: 'Anna Svensson', type: 'customer', email: 'anna@example.com' },
  { id: 2, name: 'Erik Johansson', type: 'provider', email: 'erik@example.com' },
];

const mockJobs = [
  { id: 1, customerId: 1, description: 'Gräsklippning 200 kvm', status: 'open', price: 500 },
  { id: 2, providerId: 2, customerId: 1, description: 'Gräsklippning 150 kvm', status: 'matched', price: 400 },
];

// Mock API functions
const mockAPI = {
  register: (user) => new Promise(resolve => setTimeout(() => resolve({ ...user, id: Date.now() }), 1000)),
  login: (email) => new Promise(resolve => {
    const user = mockUsers.find(u => u.email === email);
    setTimeout(() => resolve(user || null), 1000);
  }),
  createJob: (job) => new Promise(resolve => setTimeout(() => resolve({ ...job, id: Date.now() }), 1000)),
  findJobs: () => new Promise(resolve => setTimeout(() => resolve(mockJobs.filter(job => job.status === 'open')), 1000)),
  acceptJob: (jobId, providerId) => new Promise(resolve => setTimeout(() => resolve({ ...mockJobs.find(job => job.id === jobId), status: 'matched', providerId }), 1000)),
  completeJob: (jobId) => new Promise(resolve => setTimeout(() => resolve({ ...mockJobs.find(job => job.id === jobId), status: 'completed' }), 1000)),
  processPayment: (jobId, method) => new Promise(resolve => setTimeout(() => resolve({ jobId, status: 'paid', method }), 1000)),
};

const GronGardPlatform = () => {
  const [user, setUser] = useState(null);
  const [jobs, setJobs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [view, setView] = useState('welcome');
  const [formData, setFormData] = useState({ name: '', email: '', type: 'customer' });

  useEffect(() => {
    if (user) {
      setView('dashboard');
      refreshJobs();
    }
  }, [user]);

  const refreshJobs = async () => {
    setLoading(true);
    try {
      const fetchedJobs = user.type === 'customer'
        ? mockJobs.filter(job => job.customerId === user.id)
        : await mockAPI.findJobs();
      setJobs(fetchedJobs);
    } catch (err) {
      setError('Kunde inte hämta jobb. Försök igen.');
    }
    setLoading(false);
  };

  const handleAction = async (action, data = {}) => {
    setLoading(true);
    setError('');
    setSuccess('');
    try {
      switch (action) {
        case 'register':
          const newUser = await mockAPI.register(data);
          setUser(newUser);
          setSuccess('Registrering lyckades!');
          break;
        case 'login':
          const loggedInUser = await mockAPI.login(data.email);
          if (loggedInUser) {
            setUser(loggedInUser);
            setSuccess('Inloggning lyckades!');
          } else {
            setError('Användare hittades inte.');
          }
          break;
        case 'createJob':
          const newJob = await mockAPI.createJob({ customerId: user.id, ...data });
          setJobs([...jobs, newJob]);
          setSuccess('Jobbet har skapats framgångsrikt!');
          break;
        case 'acceptJob':
          const updatedJob = await mockAPI.acceptJob(data.jobId, user.id);
          setJobs(jobs.map(job => job.id === data.jobId ? updatedJob : job));
          setSuccess('Du har accepterat jobbet!');
          break;
        case 'completeJob':
          const completedJob = await mockAPI.completeJob(data.jobId);
          setJobs(jobs.map(job => job.id === data.jobId ? completedJob : job));
          setSuccess('Jobbet har markerats som slutfört!');
          break;
        case 'processPayment':
          await mockAPI.processPayment(data.jobId, data.method);
          setJobs(jobs.map(job => job.id === data.jobId ? { ...job, status: 'paid' } : job));
          setSuccess('Betalningen har genomförts!');
          break;
        default:
          throw new Error('Ogiltig åtgärd');
      }
    } catch (err) {
      setError(`Ett fel uppstod: ${err.message}`);
    }
    setLoading(false);
  };

  const renderWelcomeView = () => (
    <div className="space-y-4">
      <h2 className="text-xl font-semibold">Välkommen till GrönGård</h2>
      <p>Hitta eller erbjud gräsklippningstjänster enkelt och snabbt.</p>
      <button 
        onClick={() => setView('register')} 
        className="w-full bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 transition duration-200"
      >
        Registrera dig
      </button>
      <button 
        onClick={() => setView('login')} 
        className="w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition duration-200"
      >
        Logga in
      </button>
    </div>
  );

  const renderFormView = (formType) => (
    <form onSubmit={(e) => { e.preventDefault(); handleAction(formType, formData); }} className="space-y-4">
      {formType === 'register' && (
        <input 
          type="text" 
          placeholder="Namn" 
          value={formData.name} 
          onChange={(e) => setFormData({...formData, name: e.target.value})}
          className="w-full p-2 border rounded"
          required
        />
      )}
      <input 
        type="email" 
        placeholder="E-post" 
        value={formData.email} 
        onChange={(e) => setFormData({...formData, email: e.target.value})}
        className="w-full p-2 border rounded"
        required
      />
      {formType === 'register' && (
        <select 
          value={formData.type} 
          onChange={(e) => setFormData({...formData, type: e.target.value})}
          className="w-full p-2 border rounded"
        >
          <option value="customer">Kund</option>
          <option value="provider">Leverantör</option>
        </select>
      )}
      <button 
        type="submit" 
        className="w-full bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 transition duration-200"
      >
        {formType === 'register' ? 'Registrera' : 'Logga in'}
      </button>
    </form>
  );

  const renderDashboard = () => (
    <div className="space-y-6">
      <h2 className="text-xl font-semibold">Välkommen, {user.name}!</h2>
      {user.type === 'customer' ? renderCustomerDashboard() : renderProviderDashboard()}
    </div>
  );

  const renderCustomerDashboard = () => (
    <>
      <div className="space-y-2">
        <input 
          type="text" 
          placeholder="Beskriv ditt gräsklippningsbehov" 
          className="w-full p-2 border rounded"
          onKeyPress={(e) => e.key === 'Enter' && handleAction('createJob', { description: e.target.value, status: 'open', price: Math.floor(Math.random() * 500) + 300 })}
        />
        <button 
          onClick={() => handleAction('createJob', { description: 'Standard gräsklippning', status: 'open', price: Math.floor(Math.random() * 500) + 300 })} 
          className="w-full bg-green-500 text-white py-2 px-4 rounded hover:bg-green-600 transition duration-200"
        >
          <Clipboard className="inline-block mr-2" size={16} />
          Skapa Jobb
        </button>
      </div>
      <h3 className="text-lg font-semibold">Dina jobb:</h3>
      {renderJobList()}
    </>
  );

  const renderProviderDashboard = () => (
    <>
      <button 
        onClick={refreshJobs} 
        className="w-full bg-blue-500 text-white py-2 px-4 rounded hover:bg-blue-600 transition duration-200"
      >
        <Search className="inline-block mr-2" size={16} />
        Hitta Jobb
      </button>
      <h3 className="text-lg font-semibold">Tillgängliga jobb:</h3>
      {renderJobList()}
    </>
  );

  const renderJobList = () => (
    <ul className="space-y-4">
      {jobs.map(job => (
        <li key={job.id} className="bg-gray-100 p-4 rounded shadow">
          <p className="font-semibold">{job.description}</p>
          <p>Status: {job.status}</p>
          <p>Pris: {job.price} SEK</p>
          {renderJobActions(job)}
        </li>
      ))}
    </ul>
  );

  const renderJobActions = (job) => {
    if (user.type === 'provider' && job.status === 'open') {
      return (
        <button 
          onClick={() => handleAction('acceptJob', { jobId: job.id })} 
          className="mt-2 bg-blue-500 text-white py-1 px-2 rounded hover:bg-blue-600 transition duration-200"
        >
          Acceptera Jobb
        </button>
      );
    }
    if (user.type === 'provider' && job.status === 'matched' && job.providerId === user.id) {
      return (
        <button 
          onClick={() => handleAction('completeJob', { jobId: job.id })} 
          className="mt-2 bg-green-500 text-white py-1 px-2 rounded hover:bg-green-600 transition duration-200"
        >
          Markera som Slutfört
        </button>
      );
    }
    if (user.type === 'customer' && job.status === 'completed') {
      return (
        <div className="mt-2 space-x-2">
          <button 
            onClick={() => handleAction('processPayment', { jobId: job.id, method: 'swish' })} 
            className="bg-blue-500 text-white py-1 px-2 rounded hover:bg-blue-600 transition duration-200"
          >
            <Smartphone className="inline-block mr-1" size={16} /> Swish
          </button>
          <button 
            onClick={() => handleAction('processPayment', { jobId: job.id, method: 'card' })} 
            className="bg-green-500 text-white py-1 px-2 rounded hover:bg-green-600 transition duration-200"
          >
            <CreditCard className="inline-block mr-1" size={16} /> Kort
          </button>
        </div>
      );
    }
    return null;
  };

  return (
    <div className="max-w-md mx-auto p-6 bg-white rounded-lg shadow-md">
      <h1 className="text-2xl font-bold text-green-700 mb-6">GrönGård</h1>

      {view === 'welcome' && renderWelcomeView()}
      {view === 'register' && renderFormView('register')}
      {view === 'login' && renderFormView('login')}
      {view === 'dashboard' && renderDashboard()}

      {loading && (
        <div className="flex items-center justify-center space-x-2 mt-4">
          <Loader className="animate-spin" />
          <span>Laddar...</span>
        </div>
      )}

      {error && (
        <Alert variant="destructive" className="mt-4">
          <AlertCircle className="h-4 w-4" />
          <AlertTitle>Fel</AlertTitle>
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      {success && (
        <Alert variant="default" className="mt-4 bg-green-100 border-green-500">
          <CheckCircle className="h-4 w-4 text-green-500" />
          <AlertTitle className="text-green-700">Framgång</AlertTitle>
          <AlertDescription className="text-green-600">{success}</AlertDescription>
        </Alert>
      )}
    </div>
  );
};

export default GronGardPlatform;
