import React, { useEffect } from 'react';
import GronGardPlatform from './grongardplatform.js';
import './AppWrapper.css'; // We'll create this file for the styles

const AppWrapper = () => {
  useEffect(() => {
    // Create header grass animation
    const headerGrass = document.querySelector('.header-grass');
    for (let i = 0; i < 100; i++) {
      const headerGrassBlade = document.createElement('div');
      headerGrassBlade.className = 'header-grass-blade';
      headerGrassBlade.style.left = `${Math.random() * 100}%`;
      headerGrassBlade.style.animationDelay = `${Math.random() * 0.5}s`;
      headerGrass.appendChild(headerGrassBlade);
    }

    // Ensure footer visibility
    function ensureFooterVisibility() {
      const footer = document.querySelector('footer');
      const grassContainer = document.getElementById('grassContainer');
      const footerHeight = footer.offsetHeight;
      if (grassContainer) {
        grassContainer.style.bottom = `${footerHeight}px`;
      }
    }

    window.addEventListener('load', ensureFooterVisibility);
    window.addEventListener('resize', ensureFooterVisibility);

    return () => {
      window.removeEventListener('load', ensureFooterVisibility);
      window.removeEventListener('resize', ensureFooterVisibility);
    };
  }, []);

  return (
    <div className="container">
      <header>
        <nav>
          <div className="logo">
            <h1><span>Grön</span>Gård</h1>
          </div>
        </nav>
        <div className="header-grass"></div>
      </header>

      <main>
        <GronGardPlatform />
      </main>

      <footer>
        <p>&copy; 2024 GrönGård AB. Alla rättigheter förbehållna.</p>
      </footer>

      <div className="grass-container" id="grassContainer">
        <div className="lawnmower" id="lawnmower"></div>
      </div>
    </div>
  );
};

export default AppWrapper;
